using System;\nusing System.Data.SqlTypes;\nusing Microsoft.SqlServer.Server;\n\npublic class EmailValidation\n{\n    [SqlFunction]\n    public static SqlBoolean IsValidEmail(SqlString email)\n    {\n        try\n        {\n            var addr = new System.Net.Mail.MailAddress(email.Value);\n            return true;\n        }\n        catch\n        {\n            return false;\n        }\n    }\n}