// Assuming you have filled the DropDownList with country names\nDropDownList1.SelectedIndexChanged += new EventHandler(DropDownList1_SelectedIndexChanged);\n\nprotected void DropDownList1_SelectedIndexChanged(object sender, EventArgs e)\n{\n    // Get the selected country ID by index\n    int selectedIndex = DropDownList1.SelectedIndex;\n    string selectedCountryName = DropDownList1.SelectedItem.Text;\n\n    // Assuming you have access to your XML data structure or DataSet\n    // Extract the ID corresponding to the selected country name\n    var countryId = GetCountryIDFromXml(selectedCountryName);\n\n    // Use the ID for further processing\n    // For example, fetching more details about the selected country\n}\n\nprivate int GetCountryIDFromXml(string countryName)\n{\n    // Example logic to find and return the ID based on the country name\n    // This assumes you have parsed your XML into a List or similar structure\n    // Here's a mockup:\n    var countries = new Dictionary<string, int>\n    {\n        { "Nepal", 1 },\n        { "India", 2 },\n        { "China", 3 },\n        { "Bhutan", 4 },\n        { "USA", 5 }\n    };\n\n    return countries[countryName];\n}