using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class PasswordHasher\n{\n    // Hash a password with a unique salt\n    public static byte[] HashPassword(string password, byte[] salt)\n    {\n        using (var sha256 = SHA256.Create())\n        {\n            var hashBytes = new byte[password.Length + salt.Length];\n            Buffer.BlockCopy(Encoding.UTF8.GetBytes(password), 0, hashBytes, 0, password.Length);\n            Buffer.BlockCopy(salt, 0, hashBytes, password.Length, salt.Length);\n\n            return sha256.ComputeHash(hashBytes);\n        }\n    }\n\n    // Generate a random salt\n    public static byte[] GenerateSalt()\n    {\n        using (var rng = new RNGCryptoServiceProvider())\n        {\n            var salt = new byte[16];\n            rng.GetBytes(salt);\n            return salt;\n        }\n    }\n}