// Example: Projecting only necessary fields into a DTO (Data Transfer Object)\nvar modelData = context.RootEntities\n    .Include(re => re.RelatedEntity1)\n    .Where(re => re.SomeCondition)  // Apply filter conditions as needed\n    .Select(re => new ModelProjectionDto\n    {\n        RootId = re.Id,\n        RootName = re.Name,\n        RelatedData = re.RelatedEntity1.Select(sub => new SubEntityProjectionDto\n        {\n            Id = sub.Id,\n            Name = sub.Name\n        }).ToList()\n    })\n    .ToList();