using (var connection = new SqlConnection(connectionString))\n{\n    connection.Open();\n    var sql = "INSERT INTO YourTable (Column1, Column2) VALUES (@Value1a, @Value2a); " +\n              "INSERT INTO YourTable (Column1, Column2) VALUES (@Value1b, @Value2b); " +\n              // Add more inserts as needed\n              "INSERT INTO YourTable (Column1, Column2) VALUES (@Value1n, @Value2n)";\n\n    using (var command = new SqlCommand(sql, connection))\n    {\n        // Add parameters for each value\n        command.Parameters.AddWithValue("@Value1a", value1a);\n        command.Parameters.AddWithValue("@Value2a", value2a);\n        command.Parameters.AddWithValue("@Value1b", value1b);\n        command.Parameters.AddWithValue("@Value2b", value2b);\n        // Add more parameter assignments as needed\n\n        command.ExecuteNonQuery();\n    }\n}