protected void Session_Start(object sender, EventArgs e)\n{\n    var sessionId = Session.SessionID;\n    var currentUser = GetCurrentLoggedUser(); // Implement this method to get the current logged in user\n\n    StoreSession(sessionId, currentUser.Id);\n}\n\nprivate void StoreSession(string sessionId, int userId)\n{\n    using (var connection = new SqlConnection(ConnectionString))\n    {\n        const string query = "INSERT INTO UserSessions (UserId, SessionId) VALUES (@userId, @sessionId)";\n        connection.Open();\n        using (var command = new SqlCommand(query, connection))\n        {\n            command.Parameters.AddWithValue("@sessionId", sessionId);\n            command.Parameters.AddWithValue("@userId", userId);\n            command.ExecuteNonQuery();\n        }\n    }\n}\n\nprotected void Session_End(object sender, EventArgs e)\n{\n    var sessionId = Session.SessionID;\n\n    RemoveSession(sessionId);\n}\n\nprivate void RemoveSession(string sessionId)\n{\n    using (var connection = new SqlConnection(ConnectionString))\n    {\n        const string query = "DELETE FROM UserSessions WHERE SessionId=@sessionId";\n        connection.Open();\n        using (var command = new SqlCommand(query, connection))\n        {\n            command.Parameters.AddWithValue("@sessionId", sessionId);\n            command.ExecuteNonQuery();\n        }\n    }\n}