protected void Page_Load(object sender, EventArgs e)\n{\n    // Load the external file.\n    string filePath = Server.MapPath("Sample.htm");\n    string htmlContent = File.ReadAllText(filePath);\n\n    // Parse or find specific HTML elements if needed.\n    // Here we assume you know the exact HTML markup to look for.\n    int startIndex = htmlContent.IndexOf("<a ");\n    int endIndex = htmlContent.IndexOf("</a>", startIndex) + "</a>".Length;\n    string anchorMarkup = htmlContent.Substring(startIndex, endIndex - startIndex);\n\n    // Create a new HyperLink control.\n    var myAnchor = new HyperLink();\n    myAnchor.ID = "myAnchor";\n    // You can set other properties based on the parsed markup if needed.\n\n    // Add it to the page controls collection.\n    Page.Controls.Add(myAnchor);\n}