using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "YourConnectionStringHere";\n        string xmlData = "<Root><Item><Field1>Value1</Field1><Field2>123</Field2><Field3>Value3</Field3></Item></Root>";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand("InsertXMLData", connection);\n            command.CommandType = CommandType.StoredProcedure;\n\n            // Add XML parameter\n            XmlParameter xmlParam = new XmlParameter();\n            xmlParam.ParameterName = "@xmlData";\n            xmlParam.Value = xmlData;\n            command.Parameters.Add(xmlParam);\n\n            try\n            {\n                connection.Open();\n                int result = command.ExecuteNonQuery();\n                Console.WriteLine($"{result} rows inserted.");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n\n    // A small wrapper for adding XML type parameters if needed\n    class XmlParameter : SqlParameter\n    {\n        public XmlParameter(string parameterName, string xmlData) \n            : base(parameterName, SqlDbType.Xml) \n        { \n            Value = xmlData; \n        }\n    }\n}