protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        var uniqueId = System.Guid.NewGuid().ToString("N"); // Generate a unique ID\n        this.ClientIDMode = ClientIDMode.Static; // Ensure consistent client IDs\n        this.ID = "userControl" + uniqueId;\n\n        string script = $@"\n            var opts{uniqueId} = {{ x: 99 }};\n            var myObject{uniqueId} = new MyObject(opts{uniqueId});\n\n            document.getElementById('{this.SomeButton.ClientID}').addEventListener('click', function() {{\n                alert(myObject{uniqueId}.someFunction());\n            }});\n        ";\n\n        ClientScriptManager cs = Page.ClientScript;\n        if (!cs.IsStartupScriptRegistered("ControlUniqueScript" + uniqueId))\n        {\n            cs.RegisterStartupScript(this.GetType(), "ControlUniqueScript" + uniqueId, script, true);\n        }\n    }\n}