using System;\nusing System.IO;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\nclass FileDownloader\n{\n    private static async Task DownloadFileAsync(string url, string destinationPath)\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            try\n            {\n                Console.WriteLine($"Downloading {url} to {destinationPath}");\n                byte[] fileBytes = await client.GetByteArrayAsync(url);\n                File.WriteAllBytes(destinationPath, fileBytes);\n                Console.WriteLine("Download completed successfully.");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"An error occurred: {ex.Message}");\n            }\n        }\n    }\n\n    static async Task Main(string[] args)\n    {\n        // Define the remote URLs and local paths\n        string remoteDataUrl = "https://example.com/datafile.txt";\n        string localDataPath = @"C:\LocalServer\datafile.txt";\n\n        string remoteImageUrl = "https://example.com/image.png";\n        string localImagePath = @"C:\LocalServer\image.png";\n\n        // Download the data file\n        await DownloadFileAsync(remoteDataUrl, localDataPath);\n\n        // Download the image file\n        await DownloadFileAsync(remoteImageUrl, localImagePath);\n    }\n}