// Example of a simple Silverlight application entry point, \n// demonstrating how the codebase remains consistent across platforms.\npublic partial class App : Application\n{\n    public App()\n    {\n        this.Startup += this.Application_Startup;\n    }\n\n    private void Application_Startup(object sender, StartupEventArgs e)\n    {\n        // RootVisual is the entry point for visual rendering in Silverlight applications.\n        this.RootVisual = new MainPage();\n    }\n}