// Create a new DataTable manually\nDataTable dtDevice = new DataTable();\ndtDevice.Columns.Add("DeviceSerialNumber", typeof(string));\ndtDevice.Columns.Add("BuildID", typeof(int));\ndtDevice.Columns.Add("LEMSCredentialsID", typeof(int));\ndtDevice.Columns.Add("LEMSSoftwareID", typeof(int));\ndtDevice.Columns.Add("OwnerID", typeof(int));\ndtDevice.Columns.Add("RestagedDate", typeof(DateTime));\n\n// Execute your query and fill the DataTable\nusing (SqlConnection conn = new SqlConnection(yourConnectionString))\n{\n    using (SqlCommand cmd = new SqlCommand("SELECT DeviceSerialNumber, BuildID, LEMSCredentialsID, LEMSSoftwareID, OwnerID, RestagedDate FROM tblDevice WHERE DeviceSerialNumber = @SerialNumber", conn))\n    {\n        cmd.Parameters.AddWithValue("@SerialNumber", txtDeviceSerial.Text);\n        using (SqlDataAdapter da = new SqlDataAdapter(cmd))\n        {\n            da.Fill(dtDevice);\n        }\n    }\n}