private void Label_MouseDown(object sender, MouseButtonEventArgs e)\n{\n    // Cast the sender to a label\n    var label = (Label)sender;\n\n    // Get the position of the label relative to the screen\n    Point topLeftScreenPosition = label.PointToScreen(new Point(0, 0));\n\n    // Use this point for positioning your pop-up control\n    Console.WriteLine($"Top-Left Position: {topLeftScreenPosition}");\n}