using System;\nusing Microsoft.SharePoint;\n\npublic class ItemEditPermissionReceiver : SPItemEventReceiver\n{\n    public override void ItemUpdating(SPItemEventProperties properties)\n    {\n        if (properties.AfterProperties["Editor"] != null && !string.IsNullOrEmpty(properties.AfterProperties["Editor"].ToString()))\n        {\n            // Assume 'Author' field holds the creator of the item\n            int authorId = (int)properties.ListItem["Author"];\n            SPUser currentUser = properties.Web.CurrentUser;\n\n            if (authorId != currentUser.ID)\n            {\n                properties.Cancel = true;\n                properties.Status = SPEventReceiverStatus.CancelWithError;\n                properties.ErrorMessage = "You can only edit items you created.";\n            }\n        }\n\n        base.ItemUpdating(properties);\n    }\n}