amespace OuterNamespace\n{\n    public class MyClass\n    {\n        public static void PrintMessage()\n        {\n            System.Console.WriteLine("Hello from MyClass!");\n        }\n    }\n\n    namespace InnerNamespace\n    {\n        public class MyClass\n        {\n            public static void PrintMessage()\n            {\n                System.Console.WriteLine("Hello from InnerNamespace.MyClass!");\n            }\n        }\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        // Accessing the outer namespace's MyClass using global::\n        global::OuterNamespace.MyClass.PrintMessage(); // Outputs: Hello from MyClass!\n        \n        // Accessing the inner namespace's MyClass\n        OuterNamespace.InnerNamespace.MyClass.PrintMessage(); // Outputs: Hello from InnerNamespace.MyClass!\n    }\n}