using System;\nusing System.Windows.Forms;\n\nclass Program\n{\n    static void Main()\n    {\n        FolderBrowserDialog dialog = new FolderBrowserDialog();\n        \n        // Set the selected path to the Desktop folder\n        dialog.SelectedPath = Environment.GetFolderPath(Environment.SpecialFolder.Desktop);\n\n        // To set it to Program Files, you can use:\n        // dialog.SelectedPath = Environment.GetFolderPath(Environment.SpecialFolder.ProgramFiles);\n        \n        if (dialog.ShowDialog() == DialogResult.OK)\n        {\n            Console.WriteLine("Selected directory: " + dialog.SelectedPath);\n        }\n    }\n}