using RtfNet;\nusing System.IO;\n\npublic string ConvertRtfToText(Stream rtfStream)\n{\n    var converter = new RtfToHtmlConverter(new Rtf(), new ConverterOptions());\n    var document = converter.Convert(rtfStream, null);\n    return document.ToText();\n}\n\n// Example usage:\nvoid ConvertRtfExample()\n{\n    // Assuming you have an RTF stream, for example from a file or another source\n    using (var rtfFileStream = File.Open("example.rtf", FileMode.Open))\n    {\n        string plainText = ConvertRtfToText(rtfFileStream);\n        Console.WriteLine(plainText);\n        \n        // Or save it to a text file\n        File.WriteAllText("output.txt", plainText);\n    }\n}