using System.IO;\nusing System.Drawing;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\n\npublic void CreatePdf()\n{\n    // Step 1: Create a document-object.\n    Document document = new Document();\n\n    // Step 2: We create a writer that listens to the document\n    PdfWriter.GetInstance(document, new FileStream("HelloWorld.pdf", FileMode.Create));\n\n    // Step 3: Open the document to enable you to write to it\n    document.Open();\n\n    // Step 4: Add content to the document.\n    document.Add(new Paragraph("Hello World"));\n\n    // Adding an image\n    iTextSharp.text.Image pdfImage = iTextSharp.text.Image.GetInstance("example.jpg");\n    document.Add(pdfImage);\n\n    // Step 5: Close the document. This is mandatory to ensure all resources are released and the file is properly closed.\n    document.Close();\n}