using System;\nusing System.Collections.Generic;\n\npublic static List<IReport> InstantiateReports(List<Type> reportTypes)\n{\n    var reports = new List<IReport>();\n    foreach (Type type in reportTypes)\n    {\n        try\n        {\n            IReport report = Activator.CreateInstance(type) as IReport;\n            if (report != null)\n                reports.Add(report);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Failed to create an instance of {type.FullName}: {ex.Message}");\n        }\n    }\n\n    return reports;\n}