using System;\nusing System.IO;\nusing System.Net;\nusing System.Runtime.Serialization;\nusing System.Text;\nusing System.Xml;\n\n// Define your data contracts\n[DataContract(Name = "ActualResponse", Namespace = "http://example.com/example")]\npublic class ActualResponse\n{\n    [DataMember(Name = "ResponseItem")]\n    public ResponseItem Item { get; set; }\n}\n\n[DataContract(Name = "ResponseItem", Namespace = "http://example.com/examples")]\npublic class ResponseItem\n{\n    [DataMember(Name = "Name")]\n    public string Name { get; set; }\n\n    [DataMember(Name = "Area")]\n    public string Area { get; set; }\n}\n\n[DataContract(Name = "ErrorResponse", Namespace = "http://example.com/example")]\npublic class ErrorResponse\n{\n    [DataMember(Name = "Message")]\n    public string Message { get; set; }\n}\n\n// Method to consume REST service\npublic object ConsumeRestService(string url)\n{\n    HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n    request.Method = "GET";\n    try\n    {\n        using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n        using (Stream stream = response.GetResponseStream())\n        using (XmlDictionaryReader reader = XmlDictionaryReader.CreateTextReader(stream, new XmlDictionaryReaderQuotas()))\n        {\n            var serializer = new DataContractSerializer(typeof(ActualResponse));\n            return serializer.ReadObject(reader);\n        }\n    }\n    catch (WebException ex)\n    {\n        using (var stream = ex.Response.GetResponseStream())\n        using (var reader = XmlDictionaryReader.CreateTextReader(stream, new XmlDictionaryReaderQuotas()))\n        {\n            var errorSerializer = new DataContractSerializer(typeof(ErrorResponse));\n            return errorSerializer.ReadObject(reader);\n        }\n    }\n}