using System;\nusing System.Runtime.InteropServices;\n\npublic class HFMClientWrapper : IDisposable\n{\n    private readonly object _hfmApplication;\n\n    public HFMClientWrapper()\n    {\n        // Create an instance of the HFM application COM object.\n        _hfmApplication = Activator.CreateInstance(Type.GetTypeFromProgID("HFM.Application"));\n    }\n\n    ~HFMClientWrapper()\n    {\n        Dispose(false);\n    }\n\n    public void ExportData(string data)\n    {\n        // Use _hfmApplication here to perform export\n    }\n\n    public void Dispose()\n    {\n        Dispose(true);\n        GC.SuppressFinalize(this);\n    }\n\n    protected virtual void Dispose(bool disposing)\n    {\n        if (_hfmApplication != null)\n        {\n            while (Marshal.ReleaseComObject(_hfmApplication) > 0) { }\n            _hfmApplication = null;\n        }\n    }\n}