[assembly: System.Web.UI.WebResource("MyNamespace.MyCustomScriptControl.js", "text/javascript")]\n\nnamespace MyNamespace\n{\n    public class MyCustomScriptControl : ScriptControl\n    {\n        protected override IEnumerable<ScriptDescriptor> GetScriptDescriptors()\n        {\n            var descriptor = new ScriptControlDescriptor("MyNamespace.MyCustomScriptControl", this.ClientID);\n            yield return descriptor;\n        }\n\n        protected override IEnumerable<ScriptReference> GetScriptReferences()\n        {\n            yield return new ScriptReference("MyNamespace.MyCustomScriptControl.js", GetType().Assembly.FullName);\n        }\n    }\n}