using System;\nusing System.IO;\n\npublic class FileComparer {\n    public static bool AreFilesEqual(string file1Path, string file2Path) {\n        using (var fs1 = new FileStream(file1Path, FileMode.Open, FileAccess.Read))\n        using (var fs2 = new FileStream(file2Path, FileMode.Open, FileAccess.Read)) {\n            if (fs1.Length != fs2.Length)\n                return false;\n\n            var buffer1 = new byte[4096];\n            var buffer2 = new byte[4096];\n\n            int bytesRead1;\n            while ((bytesRead1 = fs1.Read(buffer1, 0, buffer1.Length)) > 0) {\n                int bytesRead2 = fs2.Read(buffer2, 0, buffer2.Length);\n                if (bytesRead1 != bytesRead2)\n                    return false;\n\n                for (int i = 0; i < bytesRead1; i++) {\n                    if (buffer1[i] != buffer2[i])\n                        return false;\n                }\n            }\n\n            return true;\n        }\n    }\n}