public static class ValidationExtensions\n{\n    public static MvcHtmlString ValidationSummaryWithContainer(this HtmlHelper helper, string message)\n    {\n        var validationSummary = helper.ValidationSummary(message);\n        if (!helper.ViewData.ModelState.IsValid)\n        {\n            return new MvcHtmlString($"<div class='my-validation-summary'>{validationSummary}</div>");\n        }\n        return MvcHtmlString.Empty;\n    }\n}