using System;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        string ipAddressString = "58.0.0.0";\n        IPAddress ipAddress = IPAddress.Parse(ipAddressString);\n\n        // Ensure we are dealing with an IPv4 address\n        if (ipAddress.AddressFamily == System.Net.Sockets.AddressFamily.InterNetwork)\n        {\n            byte[] ipBytes = ipAddress.GetAddressBytes();\n            uint ipIntValue = (uint)(ipBytes[0] << 24 |\n                                     ipBytes[1] << 16 |\n                                     ipBytes[2] << 8 |\n                                     ipBytes[3]);\n\n            Console.WriteLine("The integer value of the IP address is: " + ipIntValue);\n        }\n    }\n}