var result = from t1 in context.Table1\n             join t2 in context.Table2 on t1.WholesalerID equals t2.WholesalerID\n             group new { t1, t2 } by new { t1.WholesalerID, t1.RetailerID } into g\n             select new\n             {\n                 WholesalerID = g.Key.WholesalerID,\n                 RetailerID = g.Key.RetailerID,\n                 Column1Sum = g.Sum(x => x.t1.Col1),\n                 OtherColumn1Sum = g.Sum(x => x.t2.Col1),\n                 Column1Difference = g.Sum(x => x.t1.Col1) - g.Sum(x => x.t2.Col1),\n                 Column2Sum = g.Sum(x => x.t1.Col2),\n                 OtherColumn2Sum = g.Sum(x => x.t2.Col2),\n                 Column2Difference = g.Sum(x => x.t1.Col2) - g.Sum(x => x.t2.Col2)\n             };\n\nforeach (var item in result)\n{\n    Console.WriteLine($"WholesalerID: {item.WholesalerID}, RetailerID: {item.RetailerID}, " +\n                      $"Column1Sum: {item.Column1Sum}, OtherColumn1Sum: {item.OtherColumn1Sum}, " +\n                      $"Column1Difference: {item.Column1Difference}, Column2Sum: {item.Column2Sum}, " +\n                      $"OtherColumn2Sum: {item.OtherColumn2Sum}, Column2Difference: {item.Column2Difference}");\n}