using NReco.VideoConverter;\n\npublic class AudioToBitstream {\n    public void ConvertAudioToRaw(string inputFilePath, string outputFilePath) {\n        var ffMpeg = new FfMpegConverter();\n        ffMpeg.SetProgressHandler((string message) => { Console.WriteLine(message); });\n        ffMpeg.ConvertMedia(inputFilePath, outputFilePath, Format.raw);\n\n        // Note: The 'raw' format is a simple bitstream of audio data without headers.\n    }\n}