using System;\nusing System.Windows.Forms;\n\npublic class MyForm : Form {\n    private const int WM_MOUSEHWHEEL = 0x020E;\n\n    protected override void WndProc(ref Message m) {\n        base.WndProc(ref m);\n\n        // Check if the message is for horizontal mouse wheel input\n        if (m.Msg == WM_MOUSEHWHEEL) {\n            short scrollDelta = (short)((m.WParam.ToInt32() >> 16) & 0xffff);\n            Console.WriteLine("Horizontal scroll: " + scrollDelta);\n            \n            // You can add your custom handling code here.\n            // Positive scrollDelta indicates scrolling right,\n            // and negative scrollDelta indicates scrolling left.\n        }\n    }\n}