using (var stream = File.OpenRead(@"C:\path\to\yourfile.cab"))\nusing (var reader = ReaderFactory.Open(stream))\n{\n    while (reader.MoveToNextEntry())\n    {\n        if (!reader.Entry.IsDirectory)\n            reader.WriteEntryToDirectory(@"C:\output\directory", new ExtractionOptions()\n            {\n                ExtractFullPath = true,\n                Overwrite = true\n            });\n    }\n}