using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic enum Status\n{\n    Pending = 0,\n    Approved = 1,\n    Rejected = 2\n}\n\npublic void FetchDataFromDatabase()\n{\n    string connectionString = "your_connection_string_here";\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        SqlCommand command = new SqlCommand("SELECT Id, Name, StatusId FROM YourTable", connection);\n        \n        SqlDataAdapter adapter = new SqlDataAdapter(command);\n        DataSet dataSet = new DataSet();\n        adapter.Fill(dataSet, "YourDataTable");\n\n        foreach (DataRow row in dataSet.Tables["YourDataTable"].Rows)\n        {\n            int statusId = Convert.ToInt32(row["StatusId"]);\n            Status statusEnum = (Status)statusId;\n            \n            Console.WriteLine($"Name: {row["Name"]}, Status: {statusEnum}");\n        }\n    }\n}