unsafe void ExtendListWithNulls(List<object> list, int lim)\n{\n    if (lim <= list.Count) return;\n\n    // Ensure capacity has been set previously\n    list.Capacity = lim;\n\n    // Get the internal array pointer\n    object[] items = (object[])typeof(List<object>)\n        .GetField("_items", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)\n        .GetValue(list);\n\n    fixed (object* pItems = items)\n    {\n        for (int i = list.Count; i < lim; i++)\n        {\n            pItems[i] = null;\n        }\n    }\n\n    // Update count\n    typeof(List<object>)\n        .GetField("_size", System.Reflection.BindingFlags.NonPublic | System.Reflection.BindingFlags.Instance)\n        .SetValue(list, lim);\n}