// Example method for adding parameters safely handling null values\npublic void AddParameter(SqlCommand cmd, string parameterName, object value)\n{\n    if (value == null)\n    {\n        // Use DBNull.Value when the actual value is null\n        cmd.Parameters.AddWithValue(parameterName, DBNull.Value);\n    }\n    else\n    {\n        // Otherwise, add the value as is\n        cmd.Parameters.AddWithValue(parameterName, value);\n    }\n}