protected void Page_Load(object sender, EventArgs e)\n   {\n       if (!IsPostBack)\n       {\n           // Find the ObjectDataSource control by its ID\n           var ods = (ObjectDataSource)FindControl("ods");\n\n           // Set the SessionField of the first parameter\n           if (ods != null && ods.SelectParameters.Count > 0)\n           {\n               ods.SelectParameters[0].SessionField = RM.SessionParams.SESSION_PAREMETER_NAME;\n           }\n       }\n   }