using System;\nusing System.Collections.Generic;\nusing System.Data.SqlClient;\n\npublic class LookupData\n{\n    public int Id { get; set; }\n    public string Category { get; set; }\n    public double Weight { get; set; }\n}\n\npublic class Program\n{\n    private static Dictionary<int, LookupData> idLookup = new Dictionary<int, LookupData>();\n    private static Dictionary<string, LookupData> categoryLookup = new Dictionary<string, LookupData>();\n\n    public static void LoadLookupTable(string connectionString)\n    {\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            string query = "SELECT ID, Category, Weight FROM YourTableName";\n            using (SqlCommand cmd = new SqlCommand(query, conn))\n            {\n                conn.Open();\n                using (SqlDataReader reader = cmd.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        var data = new LookupData\n                        {\n                            Id = Convert.ToInt32(reader["ID"]),\n                            Category = reader["Category"].ToString(),\n                            Weight = Convert.ToDouble(reader["Weight"])\n                        };\n\n                        idLookup.Add(data.Id, data);\n                        categoryLookup[data.Category] = data; // Handles unique constraint\n                    }\n                }\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        LoadLookupTable(connectionString);\n\n        // Example of accessing data\n        if (idLookup.TryGetValue(1, out var assignmentData))\n        {\n            Console.WriteLine($"ID: {assignmentData.Id}, Category: {assignmentData.Category}, Weight: {assignmentData.Weight}");\n        }\n\n        if (categoryLookup.TryGetValue("Test", out var testData))\n        {\n            Console.WriteLine($"ID: {testData.Id}, Category: {testData.Category}, Weight: {testData.Weight}");\n        }\n    }\n}