using System;\nusing System.Drawing;\nusing Emgu.CV;\nusing Emgu.CV.CvEnum;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            VideoCapture camera = new VideoCapture(0); // 0 is usually the default camera\n\n            using (Mat frame = new Mat())\n            {\n                camera.Read(frame);\n                if (frame.Size.IsEmpty)\n                    throw new Exception("Unable to capture image.");\n\n                CvInvoke.Imshow("Camera", frame);\n\n                // Save captured frame\n                Image<Bgr, byte> img = frame.ToImage<Bgr, byte>();\n                img.Save("captured_image.png");\n\n                Console.WriteLine("Image saved as 'captured_image.png'");\n\n                CvInvoke.WaitKey(0); // Wait for user input\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}