using System;\nusing System.Data.SQLite;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Data Source=mydatabase.db;Version=3;";\n        \n        using (SQLiteConnection conn = new SQLiteConnection(connectionString))\n        {\n            conn.Open();\n            Console.WriteLine("Database connection opened.");\n\n            // Example of creating a table and inserting data\n            string createTableSQL = "CREATE TABLE IF NOT EXISTS SampleTable(Id INTEGER PRIMARY KEY, Name TEXT);";\n            using (SQLiteCommand cmd = new SQLiteCommand(createTableSQL, conn))\n            {\n                cmd.ExecuteNonQuery();\n            }\n\n            string insertDataSQL = "INSERT INTO SampleTable(Name) VALUES('SampleName');";\n            using (SQLiteCommand cmd = new SQLiteCommand(insertDataSQL, conn))\n            {\n                cmd.ExecuteNonQuery();\n            }\n        }\n    }\n}