using AutoMapper;\nusing System;\n\npublic class LINQToSQLEntity\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\npublic class YourPOCOObject\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n}\n\npublic class MappingProfile : Profile\n{\n    public MappingProfile()\n    {\n        CreateMap<LINQToSQLEntity, YourPOCOObject>();\n    }\n}\n\npublic class Program\n{\n    static void Main()\n    {\n        var config = new MapperConfiguration(cfg => cfg.AddProfile<MappingProfile>());\n        IMapper mapper = config.CreateMapper();\n\n        LINQToSQLEntity linqEntity = new LINQToSQLEntity { Id = 1, Name = "Sample" };\n        YourPOCOObject pocoObject = mapper.Map<YourPOCOObject>(linqEntity);\n\n        Console.WriteLine(pocoObject.Name);\n    }\n}