using (SqlCommand com = new SqlCommand("UPDATE Questions SET Question = @Question, Answer = @Answer, LastEdit = @LastEdit WHERE ID = @ID", connection))\n{\n    com.Parameters.AddWithValue("@Question", tbQuestion.Text);\n    com.Parameters.AddWithValue("@Answer", tbAnswer.Text);\n    com.Parameters.AddWithValue("@LastEdit", CurrentUser.Login);\n    com.Parameters.AddWithValue("@ID", CurrentQuestion.ID);\n\n    com.ExecuteNonQuery();\n}