using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new DataSet.\n        DataSet dataSet = new DataSet();\n\n        // Create two DataTables and add them to the DataSet.\n        DataTable parentTable = new DataTable("Customers");\n        DataTable childTable = new DataTable("Orders");\n\n        // Define columns for the tables.\n        parentTable.Columns.Add("CustomerID", typeof(int));\n        parentTable.Columns.Add("CustomerName", typeof(string));\n\n        childTable.Columns.Add("OrderID", typeof(int));\n        childTable.Columns.Add("CustomerID", typeof(int)); // Foreign key\n        childTable.Columns.Add("OrderDate", typeof(DateTime));\n\n        // Add the tables to the DataSet.\n        dataSet.Tables.Add(parentTable);\n        dataSet.Tables.Add(childTable);\n\n        // Create some sample data in the parent table.\n        parentTable.Rows.Add(1, "Alice");\n        parentTable.Rows.Add(2, "Bob");\n\n        // Create some sample data in the child table.\n        childTable.Rows.Add(101, 1, DateTime.Now);\n        childTable.Rows.Add(102, 1, DateTime.Now.AddDays(-1));\n        childTable.Rows.Add(103, 2, DateTime.Now);\n\n        // Define a DataRelation to relate the parent and child tables.\n        DataColumn parentColumn = parentTable.Columns["CustomerID"];\n        DataColumn childColumn = childTable.Columns["CustomerID"];\n\n        DataRelation relation = new DataRelation("CustOrders", parentColumn, childColumn);\n        dataSet.Relations.Add(relation);\n\n        // You can now navigate through the related data.\n        foreach (DataRow customerRow in parentTable.Rows)\n        {\n            Console.WriteLine($"Customer: {customerRow["CustomerName"]}");\n            foreach (DataRow orderRow in customerRow.GetChildRows(relation))\n            {\n                Console.WriteLine($"\tOrder ID: {orderRow["OrderID"]}, Order Date: {orderRow["OrderDate"]}");\n            }\n        }\n    }\n}