using System;\nusing System.IO;\nusing System.Drawing;\nusing System.Drawing.Imaging;\n\npublic class BitmapToJpegConverter\n{\n    public static byte[] ConvertBitmapToJpeg(byte[] bitmapBytes)\n    {\n        using (MemoryStream ms = new MemoryStream(bitmapBytes))\n        {\n            using (Image originalImage = Image.FromStream(ms))\n            {\n                using (MemoryStream jpegStream = new MemoryStream())\n                {\n                    // Save the image to a JPEG stream\n                    originalImage.Save(jpegStream, ImageFormat.Jpeg);\n                    \n                    // Convert the JPEG memory stream to byte array\n                    return jpegStream.ToArray();\n                }\n            }\n        }\n    }\n}