public class DirectionsViewModel\n{\n    public List<office_location> OfficeLocations { get; set; }\n    public SelectList OfficeLocationSelectList { get; set; }\n    public string SelectedAddress { get; set; } // To hold selected value\n\n    public DirectionsViewModel(List<office_location> officeLocations)\n    {\n        OfficeLocations = officeLocations;\n        OfficeLocationSelectList = new SelectList(OfficeLocations, "location_address", "location_name");\n    }\n}