using System;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        string storedProcedureName = "YourStoredProcedureName";\n\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlCommand command = new SqlCommand(storedProcedureName, connection);\n            command.CommandType = System.Data.CommandType.StoredProcedure;\n\n            connection.Open();\n            \n            // Execute the reader to retrieve the count\n            using (SqlDataReader reader = command.ExecuteReader())\n            {\n                if (reader.Read())\n                {\n                    int countValue = reader.GetInt32(0);  // Since you're returning a single integer value\n                    Console.WriteLine("Count: " + countValue);\n                }\n            }\n\n            connection.Close();\n        }\n    }\n}