using System;\nusing System.Collections.Generic;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xml = @"\n            <markup>\n                <element1 attributea=""blah"">@@value1@@</element1>\n                <element2>@@value2@@</element2>\n                <element3>\n                    <element3point1>@@value1@@</element3point1>\n                    <element3point2>@@value3@@</element3point2>\n                    <element3point3>apple</element3point3>\n                </element3>\n                <element4>pear</element4>\n            </markup>";\n\n        HashSet<string> tokensToFind = new HashSet<string> { "@@value1@@", "@@value2@@", "@@value3@@" };\n        HashSet<string> foundTokens = new HashSet<string>();\n\n        using (XmlReader reader = XmlReader.Create(new System.IO.StringReader(xml)))\n        {\n            while (reader.Read())\n            {\n                // Check if the node is a text node\n                if (reader.IsStartElement() || reader.NodeType == XmlNodeType.Text)\n                {\n                    string nodeValue = reader.Value;\n\n                    // Check against each token we are looking for\n                    foreach (string token in tokensToFind)\n                    {\n                        if (!foundTokens.Contains(token) && nodeValue.Contains(token))\n                        {\n                            foundTokens.Add(token);\n                        }\n                    }\n\n                    // Break early if all tokens are found\n                    if (foundTokens.Count == tokensToFind.Count)\n                    {\n                        break;\n                    }\n                }\n            }\n        }\n\n        Console.WriteLine("Found Tokens:");\n        foreach (var token in foundTokens)\n        {\n            Console.WriteLine(token);\n        }\n    }\n}