// UDP Server\nusing System;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\n\nclass UDPServer {\n    static void Main(string[] args) {\n        UdpClient udpServer = new UdpClient(13000);\n        IPEndPoint remoteEndPoint = null;\n        Byte[] receiveBytes = udpServer.Receive(ref remoteEndPoint);\n\n        Console.WriteLine($"Received: {Encoding.ASCII.GetString(receiveBytes)}");\n\n        udpServer.Close();\n    }\n}\n\n// UDP Client\nusing System.Net.Sockets;\nusing System.Text;\n\nclass UDPClient {\n    static void Main(string[] args) {\n        UdpClient udpClient = new UdpClient();\n        IPEndPoint serverEndPoint = new IPEndPoint(IPAddress.Parse("127.0.0.1"), 13000);\n\n        Byte[] sendBytes = Encoding.ASCII.GetBytes("Hello");\n        udpClient.Send(sendBytes, sendBytes.Length, serverEndPoint);\n    }\n}