var records = new List<YourRecordType>\n{\n    // Populate your list with records\n};\n\nusing (var writer = new DelimitedTextWriter<YourRecordType>(File.Create("output.csv")))\n{\n    writer.WriteHeader(new[] { "Field1", "Field2", /* other field names */ });\n    \n    writer.WriteRecords(records, record => new object[]\n    {\n        record.Property1,\n        record.Property2,\n        // other properties in the order you want them written\n    });\n}