using HtmlAgilityPack;\n\nclass Program\n{\n    static void Main()\n    {\n        var htmlDoc = new HtmlDocument();\n        htmlDoc.LoadHtml(@"<div id='topslot'><a><img src='example.jpg' /></a></div>");\n\n        // Use XPath to select the @src attribute of the image\n        var node = htmlDoc.DocumentNode.SelectSingleNode("//div[@id='topslot']/a/img/@src");\n\n        if (node != null)\n        {\n            string srcValue = node.Value; // This should contain 'example.jpg'\n            System.Console.WriteLine(srcValue);\n        }\n    }\n}