[WebMethod]\npublic static string YourWebMethodName()\n{\n    // Accessing HttpContext.Current is fine inside instance methods but not recommended in static methods.\n    // However, for this example, we'll assume it's necessary and handled properly.\n    var userWebSession = (UserWebSession)HttpContext.Current.Session["UserWebSession"];\n    \n    if (userWebSession != null)\n    {\n        // Use the session data\n        return userWebSession.SomeProperty;\n    }\n    else\n    {\n        return "Session not found";\n    }\n}