public class DashboardController : Controller\n{\n    public ActionResult SalesSummary()\n    {\n        var model = GetSalesSummaryData();\n        return PartialView(model);\n    }\n\n    [NonAction]\n    private SalesSummaryModel GetSalesSummaryData()\n    {\n        // Fetch and prepare data for sales summary\n        return new SalesSummaryModel { TotalSales = 25000.00M };\n    }\n\n    public ActionResult UserActivity()\n    {\n        var model = GetUserActivityData();\n        return PartialView(model);\n    }\n\n    [NonAction]\n    private UserActivityModel GetUserActivityData()\n    {\n        // Fetch and prepare data for user activity\n        return new UserActivityModel { ActiveUsersCount = 75 };\n    }\n}