using System;\nusing System.Data;\nusing System.IO;\nusing System.Xml;\n\npublic class XmlToDataSetConverter\n{\n    public static DataSet ConvertXmlDocumentToDataSet(XmlDocument xmlDoc)\n    {\n        DataSet dataSet = new DataSet();\n        using (StringReader reader = new StringReader(xmlDoc.OuterXml))\n        {\n            dataSet.ReadXml(reader);\n        }\n        return dataSet;\n    }\n\n    // Example usage:\n    public void Example()\n    {\n        XmlDocument xmlDoc = new XmlDocument();\n        // Load your XML into xmlDoc here, e.g., xmlDoc.LoadXml("<xml>...</xml>");\n\n        DataSet dataSet = ConvertXmlDocumentToDataSet(xmlDoc);\n\n        // Now you can work with the DataSet\n    }\n}