protected void Page_Load(object sender, EventArgs e)\n{\n    // Ensure validation controls are added only once per postback\n    if (!IsPostBack)\n    {\n        var rfv1 = new RequiredFieldValidator();\n        rfv1.ID = "rfv1";\n        rfv1.ControlToValidate = tb1.ID;\n        rfv1.Text = "*";\n        rfv1.Display = ValidatorDisplay.Dynamic;\n        Forms.Controls.Add(rfv1);\n    }\n}\n\nprotected void btn1_Click(object sender, EventArgs e)\n{\n    // Clear existing validation controls from the previous postback\n    var rfv1 = (RequiredFieldValidator)FindControl("rfv1");\n    if (rfv1 != null)\n    {\n        rfv1.ErrorMessage = "please enter a value for tb1";\n        rfv1.Enabled = true;\n    }\n\n    ValidationSummary1.EnableClientScript = true;\n\n    // Remove existing RegularExpressionValidator\n    var rev1 = FindControl("rev1") as RegularExpressionValidator;\n    if (rev1 != null)\n    {\n        Forms.Controls.Remove(rev1);\n    }\n\n    if (ddl1.SelectedValue == "1")\n    {\n        // Validate for number only\n        rev1 = new RegularExpressionValidator();\n        rev1.ID = "rev1";\n        rev1.ControlToValidate = tb1.ID;\n        rev1.ErrorMessage = "Please enter a numeric value.";\n        rev1.ValidationExpression = @"^\d+$";  // Regex for numbers\n        Forms.Controls.Add(rev1);\n    }\n    else if (ddl1.SelectedValue == "2")\n    {\n        // Validate for letters only\n        rev1 = new RegularExpressionValidator();\n        rev1.ID = "rev1";\n        rev1.ControlToValidate = tb1.ID;\n        rev1.ErrorMessage = "Please enter alphabetic characters only.";\n        rev1.ValidationExpression = @"^[a-zA-Z]+$";  // Regex for letters\n        Forms.Controls.Add(rev1);\n    }\n\n    Page.Validate();\n    Response.Write("<br>Page.IsValid: " + Page.IsValid);\n}